	static SERIALIZE_ATTRIBUTES = ["operator"];

	/** @inheritdoc */
	get flavor() {
		return ""; // Operator terms cannot have flavor text
	}

	/** @inheritdoc */
	get expression() {
		return ` ${this.operator} `;
	}

	/** @inheritdoc */
	get total() {
		return ` ${this.operator} `;
	}
